import React, { useEffect, useState } from "react";
import styled from "styled-components";
import SingleProduct from "./SingleProduct";
import { CircularProgress } from "@mui/material";

const Products = () => {
  // setting state
  const [data, setData] = useState([]);

  //Make two calls to API
  useEffect(() => {
    fetch(`/allInfo`)
      .then((res) => res.json())
      .then((data) => {
        //Save items to useState
        setData(data.data[0]);
      });
  }, []);

  // alphabetical order
  const ascending = () => {
    const up = data.sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });
    setData(up);
    console.log("ascending", up);
  };
  // alphabetical order reverse
  const descending = () => {
    const down = data.sort((a, b) => (a.name > b.name ? -1 : 1));
    setData(down);
    console.log("descending", down);
  };
  // // price high to low
  // const highToLow = () => {
  //   const down = data.sort((a, b) => {
  //     return (
  //       parseFloat(a.price.replace("$", "")) -
  //       parseFloat(b.price.replace("$", ""))
  //     );
  //   });
  //   setData(down);
  //   console.log("high to low", down);
  // };
  // // price low to high
  // const lowToHigh = () => {
  //   const up = data.sort((a, b) => {
  //     return (
  //       parseFloat(b.price.replace("$", "")) -
  //       parseFloat(a.price.replace("$", ""))
  //     );
  //   });
  //   setData(up);
  //   console.log("low to highs", up);
  // };

  return (
    <PageWrapper>
      <section>
        {/* <div className="column">
          <PageTitle>Products</PageTitle>
          <Select name="sortBy">
            <Option value="sortBy" selected disabled>
              Sort By
            </Option>
            <Option onClick={ascending}>Brand A-Z</Option>
            <Option onClick={descending}>Brand Z-A</Option>
            <Option onClick={highToLow}>Price: High-Low</Option>
            <Option onClick={lowToHigh}>Price: Low-High</Option>
          </Select>
        </div> */}
      </section>
      {data.length > 0 ? (
        <div className="grid">
          {data.map((product) => {
            const productName = product.productName;
            const price = product.price;
            const img = product.imageSrc;
            const productId = product.productId;
            const companyName = product.name;
            return (
              <SingleProduct
                key={productId}
                productName={productName}
                price={price}
                img={img}
                companyName={companyName}
                productId={productId}
              />
            );
          })}
        </div>
      ) : (
        <LoadingWrapper>
          <CircularProgress />
        </LoadingWrapper>
      )}
    </PageWrapper>
  );
};
const PageWrapper = styled.div`
  text-transform: uppercase;
`;
const PageTitle = styled.h1`
  font-size: var(--font-size-big);
`;

const LoadingWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100px;
`;

const Select = styled.div`
  font-family: "Roboto";
  border: 1px solid #676767;
  padding: 16px;
  font-size: 30px;
  border: none;
  cursor: pointer;
`;
const Option = styled.button`
  cursor: pointer;
  &:active {
    background: red;
  }
`;
export default Products;
