import React, { useEffect, useState, useContext } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { ShopDataContext } from "./ShopDataContext";
import { CircularProgress } from "@mui/material";
import useProductDetails from "../hooks/use-product-details.hook.js";

const ProductDetails = () => {
  const { handleAddToCart, cartItems } = useContext(ShopDataContext);
  const { id } = useParams();
  const [productDetails, setProductDetails] = useProductDetails(id);
  // console.log({ id });
  // console.log({ productDetails });

  // console.log({ cartItems });
  return productDetails ? (
    <section className="_50" style={{ height: "100vh" }}>
      <ImageBox className="column product__details--left">
        <div>
          <ProductImage src={productDetails.imageSrc} />
        </div>
      </ImageBox>
      <div className="column product__details--right">
        <CompanyDisp>{productDetails.companyName}</CompanyDisp>
        <CompanyNameDisp>{productDetails.name}</CompanyNameDisp>
        <PriceDisp>{productDetails.price} </PriceDisp>
        <div style={{ borderBottom: "1px solid black", marginTop: "20px" }} />
        <ButtonDisp>
          <AddToCartBtn onClick={() => handleAddToCart(productDetails)}>
            ADD TO CART
          </AddToCartBtn>
        </ButtonDisp>
        <div style={{ fontFamily: "Roboto" }}>
          For more information, please visit the company's{" "}
          <a href={productDetails.companyUrl} style={{ color: "black" }}>
            website
          </a>
        </div>
      </div>
    </section>
  ) : (
    <LoadingWrapper>
      <CircularProgress />
    </LoadingWrapper>
  );
};

const ImageBox = styled.div`
  /* display: flex;
  align-items: center; */
`;

const ProductImage = styled.img`
  /* width: 40vh; */
  @media (max-width: 520px) {
    width: 100%;
  } ;
`;

const CompanyDisp = styled.div`
  font-weight: bold;
  font-size: 40px;
  text-transform: uppercase;
`;

const CompanyNameDisp = styled.div`
  font-size: 20px;
  padding-bottom: 10px;
`;

const PriceDisp = styled(CompanyNameDisp)`
  padding-bottom: 15px;
  font-size: 40px;
`;

const ButtonDisp = styled.div`
  margin-top: 40px;
  margin-bottom: 20px;
`;

const AddToCartBtn = styled.button`
  cursor: pointer;
  width: 100%;
  height: 50px;
  background-color: black;
  color: white;
  border-radius: 5px;
  border: none;
`;

const LoadingWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100px;
`;

export default ProductDetails;
