import React, { useEffect, useState } from "react";
import styled from "styled-components";
import SingleProduct from "./SingleProduct";
import { CircularProgress } from "@mui/material";

const Products = () => {
  // setting state
  const [items, setItems] = useState(null);
  const [companies, setCompanies] = useState(null);
  // const [products, setProducts] = useState(null);

  // importing all products
  const findProducts = async () => {
    const response = await fetch("/items");
    const data = await response.json();
    setItems(data.data);
  };

  // importing company
  const findCompanies = async () => {
    const response = await fetch("/companies");
    const data = await response.json();
    setCompanies(data.data);
  };

  // Calling data
  useEffect(async () => {
    await findProducts();
    await findCompanies();
  }, []);

  let products = [];
  if (items && companies) {
    // changing key name for the items to "productName" so it won't override with the companies "name" data. Since the keys are the same
    const itemsProducts = items.map(({ name: productName, ...rest }) => ({
      productName,
      ...rest,
    }));

    // merging items to the corresponding companies data set
    let newArray = itemsProducts.map((element, index) =>
      Object.assign({}, element, companies[index])
    );
    products.push(newArray);

    // alphabetical order
    newArray.sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });
  }

  return (
    <PageWrapper>
      <section>
        <div className="column">
          <PageTitle>Products</PageTitle>
        </div>
      </section>

      {items ? (
        <div className="grid">
          {items.map((item) => {
            const itemName = item.name;
            const itemPrice = item.price;
            const itemImg = item.imageSrc;
            const companyId = item.companyId;
            const _id = item._id;
            return (
              <SingleProduct
                key={_id}
                itemName={itemName}
                itemPrice={itemPrice}
                itemImg={itemImg}
                companyId={companyId}
                _id={_id}
              />
            );
          })}
        </div>
      ) : (
        <CircularProgress />
      )}
    </PageWrapper>
  );
};
const PageWrapper = styled.div`
  text-transform: uppercase;
`;
const PageTitle = styled.h1`
  font-size: 40px;
`;

export default Products;
