import React, { useEffect, useState } from "react";
import styled from "styled-components";
import SingleProduct from "./SingleProduct";
import { CircularProgress } from "@mui/material";

const Products = () => {
  // setting state
  const [items, setItems] = useState([]);
  const [companies, setCompanies] = useState([]);
  let productsArray = [];

  useEffect(() => {
    fetch(`/items`)
      .then((res) => res.json())
      .then((data) => {
        setItems(data.data);

        return fetch(`/companies`)
          .then((res) => res.json())
          .then((data) => {
            setCompanies(data.data);
          });
      });
  }, []);

  if (items.length > 0 && companies.length > 0) {
    // changing key name for the items to "productName" so it won't override with the companies "name" data. Since the keys are the same
    const itemsProducts = items.map(({ name: productName, ...rest }) => ({
      productName,
      ...rest,
    }));

    // merging items to the corresponding companies data set
    let newArray = itemsProducts.map((element, index) =>
      Object.assign({}, element, companies[index])
    );
    productsArray.push(newArray);

    // alphabetical order
    newArray.sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });

    productsArray = newArray;
  }

  return (
    <PageWrapper>
      <section>
        <div className="column">
          <PageTitle>Products</PageTitle>
        </div>
      </section>
      {productsArray.length > 0 ? (
        <div className="grid">
          {productsArray.map((product) => {
            const productName = product.productName;
            const price = product.price;
            const img = product.imageSrc;
            const _id = product._id;
            const companyName = product.name;

            return (
              <SingleProduct
                key={_id}
                productName={productName}
                price={price}
                img={img}
                companyName={companyName}
                _id={_id}
              />
            );
          })}
        </div>
      ) : (
        <CircularProgress />
      )}
    </PageWrapper>
  );
};
const PageWrapper = styled.div`
  text-transform: uppercase;
`;
const PageTitle = styled.h1`
  font-size: 40px;
`;

export default Products;
