import React, { useContext, useState, useEffect } from "react";
import { ShopDataContext } from "./ShopDataContext";
import { v4 as uuidv4 } from "uuid";
import styled from "styled-components";

const Cart = () => {
  const { cartItems } = useContext(ShopDataContext);
  const [priceTotal, setPriceTotal] = useState(0);

  useEffect(() => {
    let totalPrice = 0;
    cartItems.forEach((cartItem) => {
      let price = cartItem.price;
      totalPrice = totalPrice + parseFloat(price.replace(/\$/gi, ''));
    });
    setPriceTotal(totalPrice);
  }, []);

  return (
    <section className="_66_33">
      <div className="column_left">
        <CartH1>YOUR CART</CartH1>
        {cartItems.map((cartItem) => {
          return (
            <div key={uuidv4()}>
              <div className="grid cart_page">
                <div><div><img src={cartItem.imageSrc}></img></div></div>
                <div>
                  <Company>{cartItem.companyName}</Company>
                  <Description>
                    {cartItem.name}
                  </Description>
                  <Price>{cartItem.price}</Price>
                  <CartBottom>
                    <div>
                      <Minus>-</Minus> 1 <Plus>+</Plus>
                    </div>
                    <Remove>Remove</Remove>
                  </CartBottom>
                </div>
              </div>
              <hr></hr>
            </div>
          );
        })}
      </div>
      <div className="column_right">
        <TotalCost>
          <div>Total cost:</div>
          <div>${`${parseFloat(priceTotal).toFixed(2)}`}</div>
        </TotalCost>
        <Purchase>PURCHASE</Purchase>
      </div>
    </section>
  );
};

const Company = styled.div`
  font-size: var(--font-size-medium);
`;

const Description = styled.div`
  font-size: var(--font-size-small);
`;
const Price = styled.div`
  font-size: var(--font-size-medium);
  margin-top: 10px;
`;

const Purchase = styled.button`
  color: white;
  background: black;
  width: 100%;
  font-size: var(--font-size-medium);
  border-radius: 4px;
  border: none;
  font-family: "Oswald", "Roboto", sans-serif;
  padding: 10px 0;
`;

const TotalCost = styled.div`
  display: flex;
  justify-content: space-between;
  text-decoration: bold;
  font-size: var(--font-size-medium);
  margin-bottom: 20px;
`;

const CartBottom = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: var(--font-size-small);
  margin-top: 20px;
`;

const Minus = styled.button`
  background: white;
  border: none;
  cursor: pointer;
`;

const Plus = styled.button`
  background: white;
  border: none;
  cursor: pointer;
`;

const Remove = styled.button`
  background: white;
  border: none;
  text-decoration: underline;
  cursor: pointer;
`;

const CartH1 = styled.h1`
  margin-bottom: 30px;
`;

export default Cart;
