import React from "react";
import styled from "styled-components";
import { AiOutlineShopping } from "react-icons/ai";
import { NavLink } from "react-router-dom";

const Header = () => {
  return (
    <HeaderElement>
      <h1>Five</h1>
      <Menu>
        <a className="product_button" href="/products">
          Products
        </a>
        <a className="cart_button" href="">
          <AiOutlineShopping />
        </a>
      </Menu>
    </HeaderElement>
  );
};

const HeaderElement = styled.header`
  align-items: center;
  padding: 20px 30px;
  display: flex;
  max-width: 1280px;
  margin-left: auto;
  margin-right: auto;
  h1 {
    text-transform: uppercase;
    font-size: 50px;
  }
`;

const Menu = styled.div`
  a.product_button {
    text-decoration: none;
    color: #000;
  }
  justify-content: flex-end;
  display: flex;
  flex: 1;
  .cart_button {
    svg {
      color: #000;
      font-size: 20px;
      margin-left: 20px;
    }
  }
`;

export default Header;
