import React, { useContext } from "react";
import styled from "styled-components";
import { AiOutlineShopping } from "react-icons/ai";
import { ShopDataContext } from "./ShopDataContext";
import { NavLink } from "react-router-dom";

const Header = () => {
  const { cartItems } = useContext(ShopDataContext);

  return (
    <HeaderElement>
      <h1>Five</h1>
      <Menu>
        <a className="product_button">Products</a>
        <a className="cart_button">
          <AiOutlineShopping />
          {cartItems.length > 0 ? (
            <ItemCounter>{cartItems.length}</ItemCounter>
          ) : (
            <></>
          )}
        </a>
      </Menu>
    </HeaderElement>
  );
};

const HeaderElement = styled.header`
  align-items: center;
  padding: 20px 30px;
  display: flex;
  max-width: 1280px;
  margin-left: auto;
  margin-right: auto;
  h1 {
    text-transform: uppercase;
    font-size: 50px;
  }
`;

const Menu = styled.div`
  a.product_button {
    text-decoration: none;
    color: #000;
  }
  justify-content: flex-end;
  display: flex;
  flex: 1;
  .cart_button {
    position: relative;
    svg {
      color: #000;
      font-size: 20px;
      margin-left: 20px;
    }
  }
`;

const ItemCounter = styled.span`
  position: absolute;
  right: -8px;
  color: #000;
  width: 17px;
  border-radius: 50px;
  top: -4px;
  background-color: rgba(255, 255, 255, 0.9);
  border: 1px solid #757d8a;
  text-align: center;
  font-size: 12px;
  font-weight: bold;
`;

export default Header;
