import { createGlobalStyle } from "styled-components";

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    font-family: 'Oswald', 'Roboto', sans-serif;
    color: black;
  }
  div, h1, h2, h2, p{
    margin: 0;
    padding: 0;
    font-family: 'Oswald', 'Roboto', sans-serif;
    color: black;
  }
`;

export default GlobalStyle;
