import React, { useEffect, useState } from "react";
import styled from "styled-components";
import SingleProduct from "./SingleProduct";
import { CircularProgress } from "@mui/material";

const Products = () => {
  // setting state
  const [items, setItems] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [sortedData, setSortedData] = useState([]);
  const [data, setData] = useState([]);
  let productsArray = [];

  //Make two calls to API
  useEffect(() => {
    fetch(`/allInfo`)
      .then((res) => res.json())
      .then((data) => {
        //Save items to useState
        setItems(data.data[0]);
      });
  }, []);

  // alphabetical order
  const ascending = () => {
    items.sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });
    setSortedData(items);
    console.log(items);
  };

  const descending = () => {
    items.sort((a, b) => (a.name > b.name ? -1 : 1));
    setSortedData(items);
  };
  return (
    <PageWrapper>
      <section>
        <div className="column">
          <PageTitle>Products</PageTitle>
          <Select name="sortBy">
            <Option value="sortBy" selected disabled>
              Sort By
            </Option>
            <Option value="brand-A-Z">Brand A-Z</Option>
            <Option value="brand-A-Z" onSelect={descending}>
              Brand Z-A
            </Option>
            <Option value="high-low">Price: High-Low</Option>
            <Option value="low-high">Price: Low-High</Option>
          </Select>
        </div>
      </section>
      {items.length > 0 ? (
        <div className="grid">
          {items.map((product) => {
            const productName = product.productName;
            const price = product.price;
            const img = product.imageSrc;
            const productId = product.productId;
            const companyName = product.name;
            return (
              <SingleProduct
                key={productId}
                productName={productName}
                price={price}
                img={img}
                companyName={companyName}
                productId={productId}
              />
            );
          })}
        </div>
      ) : (
        <LoadingWrapper>
          <CircularProgress />
        </LoadingWrapper>
      )}
    </PageWrapper>
  );
};
const PageWrapper = styled.div`
  text-transform: uppercase;
`;
const PageTitle = styled.h1`
  font-size: var(--font-size-big);
`;

const LoadingWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100px;
`;

const Select = styled.select`
  width: 180px;
  height: 30px;
  font-size: 20px;
  border: 1px solid #676767;
`;
const Option = styled.option``;
export default Products;
