import React, { useEffect, useState, useContext } from "react";
import styled from "styled-components";
import { useParams } from "react-router-dom";
import { ShopDataContext } from "./ShopDataContext";

const ProductDetails = () => {

  const { product, setProduct, handleAddToCart, cartItems } = useContext(ShopDataContext);
  const [company, setCompany] = useState([]);

  const { id } = useParams();

  // importing product into a useState
  const ProductDetailsFetch = async () => {
    const response = await fetch(`/items/${id}`);
    const data = await response.json();
    CompanyFetch(data.data.companyId);
    setProduct(data.data);
  };

  const CompanyFetch = async (companyID) => {
    const response = await fetch(`/companies/${companyID}`);
    const data = await response.json();
    setCompany(data.data);
  };

  useEffect(async () => {
    await ProductDetailsFetch();
  }, [id]);

  return (
    <section className="_50" style={{ height: "100vh" }}>
      <ImageBox className="column">
        <ProductImage src={product.imageSrc} />
      </ImageBox>
      <div
        className="column"
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
        }}
      >
        <CompanyDisp>{company.name}</CompanyDisp>
        <CompanyNameDisp>{product.name}</CompanyNameDisp>
        <PriceDisp>{product.price} </PriceDisp>
        <div style={{ borderBottom: "1px solid black", marginTop: "20px" }} />
        <ButtonDisp>
          <AddToCartBtn onClick={handleAddToCart}>ADD TO CART</AddToCartBtn>
        </ButtonDisp>
        <div style={{ fontFamily: "Roboto" }}>
          For more information, please visit the company's{" "}
          <a href={company.url} style={{ color: "black" }}>
            website
          </a>
        </div>
      </div>
    </section>
  );
};

const ImageBox = styled.div`
  display: flex;
  align-items: center;
`;

const ProductImage = styled.img`
  width: 40vh;
  @media (max-width: 520px) {
    width: 100%;
  } ;
`;

const CompanyDisp = styled.div`
  font-weight: bold;
  font-size: 40px;
  text-transform: uppercase;
`;

const CompanyNameDisp = styled.div`
  font-size: 20px;
  padding-bottom: 10px;
`;

const PriceDisp = styled(CompanyNameDisp)`
  padding-bottom: 15px;
  font-size: 40px;
`;

const ButtonDisp = styled.div`
  margin-top: 40px;
  margin-bottom: 20px;
`;

const AddToCartBtn = styled.button`
  cursor: pointer;
  width: 100%;
  height: 50px;
  background-color: black;
  color: white;
  border-radius: 5px;
`;

export default ProductDetails;
