import React, { useContext } from "react";
import styled from "styled-components";
import { ShopDataContext } from "./ShopDataContext";
import { v4 as uuidv4 } from "uuid";

const Cart = () => {
  const { handleAddToCart, cartItems } = useContext(ShopDataContext);

  // create a compact cart array, where each item from the cart is included only once, with an added "quantity" property, so that if there is more than one of the item, the quantity is increased
  let compactCart = [];
  cartItems.forEach((item) => {
    // if the cart item already exists in the compact cart array, find its index
    const indexOfItem = compactCart.findIndex(
      (ccItem) => ccItem._id == item._id
    );

    if (indexOfItem === -1) {
      // if it's not already in the compact cart, add it in, with a quantity of 1
      compactCart.push({ ...item, quantity: 1 });
    } else {
      // if it is already in the compact cart, increase its quantity by 1
      compactCart[indexOfItem].quantity++;
    }
  });

  // TODO this function gets a cart item and modifies it into a form through which it can be used by handleAddToCart function. Maybe it would be better to modify the cartItems array instead so that it has the same form as the compactCart array in this component?
  const getCartItem = () => {};

  const handlePurchase = () => {
    console.log({ compactCart });
    const cartForServer = compactCart.map((item) => {
      return { itemId: item._id, quantity: item.quantity };
    });
    console.log({ cartForServer });
    fetch(`/items/buy`, {
      method: "PUT",
      body: JSON.stringify({
        _id: uuidv4(),
        cart: cartForServer,
      }),
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((json) => {
        // console.log(json);
        const { status, message, data } = json;

        if (status == 202) {
          // TODO useHistory or useNavigate to redirect the user to the home page, maybe with a message saying your items were purchased? Also, make cart empty
          console.log("Successfully purchased items");
        } else {
          console.log("There was an error", { status, message, data });
        }
      });
  };

  // calculate total cost of all the cart items
  const totalCost =
    Math.round(
      compactCart
        .map((item) => Number(item.price.split("$")[1]) * item.quantity)
        .reduce((acc, curr) => acc + curr, 0) * 100
    ) / 100;

  return (
    <section className="_66_33">
      <div className="column_left">
        <CartH1>YOUR CART</CartH1>
        {compactCart.map((item, key) => {
          return (
            <div key={key}>
              <div className="grid cart_page">
                <div className="grid__left">
                  <ProductImage className="product__image" src={item.imageSrc} />
                </div>
                <div className="grid__right">
                  <Company>{item.companyName}</Company>
                  <ProductName>{item.name}</ProductName>
                  <Price>{item.price}</Price>
                  <CartBottom>
                    <div>
                      <Minus>-</Minus> {item.quantity} <Plus>+</Plus>
                    </div>
                    <Remove
                      onClick={() => {
                        console.log("Clicked REMOVE");
                      }}
                    >
                      Remove
                    </Remove>
                  </CartBottom>
                </div>
              </div>
              <hr></hr>
            </div>
          );
        })}
      </div>
      <div className="column_right">
        <TotalCost>
          <div>Total cost:&nbsp;</div>
          <div>${totalCost}</div>
        </TotalCost>
        <Purchase onClick={() => handlePurchase()}>PURCHASE</Purchase>
      </div>
    </section>
  );
};

const Company = styled.div`
  font-size: var(--font-size-medium);
`;

const ProductName = styled.div`
  font-size: var(--font-size-small);
`;
const Price = styled.div`
  font-size: var(--font-size-medium);
  margin-top: 10px;
`;

const Purchase = styled.button`
  color: white;
  background: black;
  width: 100%;
  font-size: var(--font-size-medium);
  border-radius: 4px;
  border: none;
  font-family: "Oswald", "Roboto", sans-serif;
  padding: 10px 0;
  cursor: pointer;
`;

const TotalCost = styled.div`
  display: flex;
  white-space: nowrap;
  justify-content: space-between;
  text-decoration: bold;
  font-size: var(--font-size-medium);
  margin-bottom: 20px;
`;

const CartBottom = styled.div`
  display: flex;
  justify-content: space-between;
  font-size: var(--font-size-small);
  margin-top: 20px;
`;

const Minus = styled.button`
  background: white;
  border: none;
  cursor: pointer;
`;

const Plus = styled.button`
  background: white;
  border: none;
  cursor: pointer;
`;

const Remove = styled.button`
  background: white;
  border: none;
  text-decoration: underline;
  cursor: pointer;
`;

const ProductImage = styled.img``;

const CartH1 = styled.h1`
  margin-bottom: 30px;
`;

export default Cart;
